const loadEnvVariables=async()=>{try{const t=await fetch("/.env"),n=await t.text(),i={};return n.split("\n").forEach(t=>{if((t=t.trim())&&!t.startsWith("#")){const[n,r]=t.split("=");n&&r&&(i[n.trim()]=r.trim())}}),i}catch(t){return console.error("Error loading environment variables:",t),{}}};export const config={env:null,async init(){this.env=await loadEnvVariables()},get(t){return this.env?.[t]??null}};config.init();